import React, { useState } from "react";

function SubmitRequest() {
  const [form, setForm] = useState({
    type: "Academic",
    priority: "Low",
    description: "",
    file: null,
  });

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === "file") {
      setForm({ ...form, file: files[0] });
    } else {
      setForm({ ...form, [name]: value });
    }
  };

  const handleSubmit = () => {
    alert(`Support request submitted!\nType: ${form.type}\nPriority: ${form.priority}`);
    // You could later send this data to backend via fetch or axios
  };

  return (
    <div className="p-4 max-w-lg mx-auto">
      <h2 className="text-2xl font-bold mb-4 text-center">Submit a Support Request</h2>

      <label className="block mb-1 font-medium">Request Type</label>
      <select
        name="type"
        value={form.type}
        onChange={handleChange}
        className="border w-full p-2 mb-3 rounded"
      >
        <option value="Academic">Academic</option>
        <option value="Technical">Technical</option>
        <option value="Other">Other</option>
      </select>

      <label className="block mb-1 font-medium">Priority</label>
      <select
        name="priority"
        value={form.priority}
        onChange={handleChange}
        className={`border w-full p-2 mb-3 rounded ${
          form.priority === "High"
            ? "border-red-500"
            : form.priority === "Medium"
            ? "border-yellow-500"
            : "border-green-500"
        }`}
      >
        <option value="Low">Low</option>
        <option value="Medium">Medium</option>
        <option value="High">High</option>
      </select>

      <label className="block mb-1 font-medium">Description</label>
      <textarea
        name="description"
        value={form.description}
        onChange={handleChange}
        placeholder="Describe your issue in detail..."
        className="border w-full p-2 mb-3 rounded resize-y min-h-[120px]"
      />

      <label className="block mb-1 font-medium">Attach File (optional)</label>
      <input
        type="file"
        name="file"
        onChange={handleChange}
        accept=".jpg,.jpeg,.png,.pdf,.docx"
        className="mb-4"
      />

      <button
        onClick={handleSubmit}
        className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 w-full rounded"
      >
        Submit Request
      </button>
    </div>
  );
}

export default SubmitRequest;
